tinymce.init({
    selector: '#descripcion-txt',
    height: 100,
    menubar: false,
    plugins: [
      'advlist autolink lists link image charmap print preview anchor',
      'searchreplace visualblocks code fullscreen',
      'insertdatetime media table paste code help wordcount'
    ],
    toolbar: 'undo redo | formatselect | ' +
    'bold italic backcolor | alignleft aligncenter ' +
    'alignright alignjustify | bullist numlist outdent indent | ' +
    'removeformat | help',
    content_style: 'body { font-family:Helvetica,Arial,sans-serif; font-size:14px }'
  });
// arreglo
const Menus = [];

//Funciones-----------------------------------------------------------------
const cargarTabla = ()=>{
    let tbody = document.querySelector("#table-tbody");
    tbody.innerHTML = "";

    for(let i= 0 ; i < Menus.length ; i++){
        let m = Menus[i];

        let tr = document.createElement("tr");

        let tdNom = document.createElement("td");
        tdNom.innerText = m.nombre;
        let tdHor = document.createElement("td");
        tdHor.innerText = m.horario;
        let tdVal = document.createElement("td");
        tdVal.innerText = "$"+ m.valor;
        let tdDes = document.createElement("td");
        tdDes.innerHTML = m.descripcion;
        let tdOfe = document.createElement("td");
        let img = new Image();
        img.classList.add("img-fluid");
        img.width = 100;
        img.height = 100;

        if((m.horario == "Desayuno") && (m.valor < 5000 )){
            img.src = "data:image/jpeg;base64," ;
        }else if((m.horario == "Almuerzo") && (m.valor < 15000 )){
            img.src = "data:image/jpeg;base64," ;
        }else if((m.horario == "Once") && (m.valor < 10000 )){
            img.src = "data:image/jpeg;base64," ;
        }else if((m.horario == "Cena") && (m.valor < 20000 )){
            img.src = "data:image/jpeg;base64," ;
        }else{
            img.src = "https://us.123rf.com/450wm/thawats/thawats1407/thawats140700151/30534082-no-sign-on-white-background.jpg?ver=6" ;
        }

        tdOfe.appendChild(img);

        tr.appendChild(tdNom);
        tr.appendChild(tdHor);
        tr.appendChild(tdVal);
        tr.appendChild(tdDes);
        tr.appendChild(tdOfe);

        tbody.appendChild(tr);

    }

};


// crear select
let horarios = ["Desayuno" , "Almuerzo" , "Once" , "Cena"];
let select = document.querySelector("#horario-select");
select.classList.add("form-select")
for(let i = 0 ; i < horarios.length ; i++ ){
    let option = document.createElement("option");
    option.innerText = horarios[i];
    select.appendChild(option);
}
// evento click select
document.querySelector("#horario-select").addEventListener("click" , ()=>{
    let horario = document.querySelector("#horario-select").value;
    let valor = document.querySelector("#valor-num");
    // restringir valor desayuno
    if(horario == "Desayuno"){
        valor.value = 1000;
        valor.min = 1000;
        valor.max = 10000;
    }else if(horario == "Almuerzo"){
        valor.value = 10000;
        valor.min = 10000;
        valor.max = 20000;
    }else if(horario == "Once"){
        valor.value = 5000;
        valor.min = 5000;
        valor.max = 15000;
    }else if(horario == "Cena"){
        valor.value = 16000;
        valor.min = 16000;
        valor.max = 100000000000000000000000000000000000000;
    }

});


//evento click boton registrar
document.querySelector("#registrar-btn").addEventListener("click" , ()=>{
    let nombre  = document.querySelector("#nombre-txt").value ;
    let horario = document.querySelector("#horario-select").value;
    let valor   = document.querySelector("#valor-num").value;
    let descripcion = tinymce.get("descripcion-txt").getContent();


    
    let Menu = {};
    Menu.nombre  = nombre ;
    Menu.horario = horario;
    Menu.valor   = valor;
    Menu.descripcion = descripcion

    Menus.push(Menu);

    cargarTabla();
    
    Swal.fire("Exito!","Menu Registrado" , "success");

    document.querySelector("#nombre-txt").value = "" ;
    document.querySelector("#horario-select").value = "Desayuno";
    document.querySelector("#valor-num").value = 1000;
    tinymce.get("descripcion-txt").setContent("");
    
    
});

